$.validator.setDefaults({ highlight: function (r) { $(r).closest(".form-group").addClass("has-error") }, unhighlight: function (r) { $(r).closest(".form-group").removeClass("has-error") }, errorElement: "span", errorClass: "help-block", errorPlacement: function (r, e) { r.insertAfter(e.parent(".input-group").length ? e.parent() : e) } });